    } else {
      return null;
    }
  }

  function processWikiTree(tree, hasWikiLinkRule) {
    var stack = [].concat(tree);

    var mergeable = function (node) {
      return (
        node.type === 'element' &&
        node.tag === 'p' &&
        (!node.attributes || Object.keys(node.attributes).length === 0)
